import React, { useEffect, useRef, useState } from "react";
import Timer from "./Timer";

const Map = ({ markers }) => {
  const mapRef = useRef(null);
  const googleMap = useRef(null);
  const markerInstances = useRef([]);
  const userCircle = useRef(null);
  const [userLocation, setUserLocation] = useState(null);
  const [showButton, setShowButton] = useState(false);
  const [showForm, setShowForm] = useState(false);
  const [reason, setReason] = useState("");
  const [timerKey, setTimerKey] = useState(0); // To reset the Timer

  function initializeMap(latitude, longitude) {
    if (!googleMap.current) {
      googleMap.current = new window.google.maps.Map(mapRef.current, {
        center: { lat: latitude, lng: longitude },
        zoom: 17,
      });
    }
  }

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          setUserLocation({ lat: latitude, lng: longitude });
          initializeMap(latitude, longitude);
          updateUserCircle(latitude, longitude);
        },
        (error) => {
          console.error("Error getting location: ", error.message);
          alert("Unable to retrieve your location. Using default location.");
          initializeMap(37.7749, -122.4194);
        }
      );
    } else {
      alert("Geolocation is not supported by this browser.");
      initializeMap(37.7749, -122.4194);
    }
  }, []);

  function updateUserCircle(lat, lng) {
    if (!googleMap.current) return;

    if (!userCircle.current) {
      userCircle.current = new window.google.maps.Circle({
        center: { lat, lng },
        radius: 20,
        strokeColor: "blue",
        strokeOpacity: 0.5,
        strokeWeight: 2,
        fillColor: "blue",
        fillOpacity: 0.3,
        map: googleMap.current,
      });
    } else {
      userCircle.current.setCenter({ lat, lng });
    }
  }

  useEffect(() => {
    if (googleMap.current && markers) {
      markerInstances.current.forEach((marker) => marker.setMap(null));
      markerInstances.current = [];

      markers.forEach((marker) => {
        const newMarker = new window.google.maps.Marker({
          position: marker,
          map: googleMap.current,
        });

        // Add click event to marker
        newMarker.addListener("click", () => {
          setShowButton(true);
          setShowForm(false);
        });

        markerInstances.current.push(newMarker);
      });

      console.log("Number of markers on the map: ", markerInstances.current.length);
    }
  }, [markers]);

  const handleShowForm = () => {
    setShowButton(false);
    setShowForm(true);
    setTimerKey((prev) => prev + 1); // Reset Timer when showing form
  };

  const handleOkClick = () => {
    console.log("Parking Reason:", reason);
    setShowForm(false);
  };

  return (
    <div style={{ position: "relative" }}>
      <div ref={mapRef} style={{ width: "100%", height: "500px" }} />

      {/* Show button near user location */}
      {showButton && userLocation && (
        <button
          style={{
            position: "absolute",
            top: "80%",
            left: "50%",
            transform: "translate(-50%, -50%)",
            padding: "10px",
            background: "blue",
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
          onClick={handleShowForm}
        >
          Open Parking Form
        </button>
      )}

      {/* Show the form when the button is pressed */}
      {showForm && (
        <div
          style={{
            position: "absolute",
            top: "60%",
            left: "50%",
            transform: "translate(-50%, -50%)",
            background: "white",
            padding: "20px",
            borderRadius: "10px",
            boxShadow: "0px 4px 6px rgba(0,0,0,0.1)",
            display: "flex",
            flexDirection: "column",
            gap: "10px",
            width: "250px",
          }}
        >
          <label>Reason for Parking:</label>
          <input
            type="text"
            value={reason}
            onChange={(e) => setReason(e.target.value)}
            style={{ padding: "5px" }}
          />
          <Timer key={timerKey} />
          <button onClick={handleOkClick} style={{ padding: "10px", background: "green", color: "white", border: "none", borderRadius: "5px" }}>
            OK
          </button>
        </div>
      )}
    </div>
  );
};

export default Map;
