import React, { useEffect, useState } from "react";

const Timer = ({ reason, timerValue, position, onRemove }) => {
  const [timeLeft, setTimeLeft] = useState(timerValue * 60); // Convert minutes to seconds

  useEffect(() => {
    if (timeLeft <= 0) {
      onRemove(); // Remove the box when time expires
      return;
    }

    const timer = setInterval(() => {
      setTimeLeft((prev) => prev - 1);
    }, 1000);

    return () => clearInterval(timer);
  }, [timeLeft, onRemove]);

  // Ensure position is defined before rendering
  if (!position || typeof position.x === "undefined" || typeof position.y === "undefined") {
    return null; // Don't render anything if position is missing
  }

  return (
    <div
      style={{
        position: "absolute",
        top: position.y || "50%", // Default value if undefined
        left: position.x || "50%", // Default value if undefined
        transform: "translate(-50%, -50%)",
        backgroundColor: "white",
        padding: "10px",
        border: "1px solid black",
        borderRadius: "5px",
        zIndex: 1000,
      }}
    >
      <p><strong>Reason:</strong> {reason}</p>
      <p><strong>Time Left:</strong> {Math.floor(timeLeft / 60)} min {timeLeft % 60} sec</p>
    </div>
  );
};

export default Timer;
