import React, { useEffect, useRef, useState } from "react";
import Timer from "./Timer";

const Map = () => {
  const mapRef = useRef(null);
  const googleMap = useRef(null);
  const [markedLocations, setMarkedLocations] = useState([]);

  function initializeMap(latitude, longitude) {
    if (!googleMap.current) {
      googleMap.current = new window.google.maps.Map(mapRef.current, {
        center: { lat: latitude, lng: longitude },
        zoom: 17,
      });

      // Add event listener to place a marker and show input form
      googleMap.current.addListener("click", (event) => {
        handleMapClick(event.latLng);
      });
    }
  }

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.watchPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          initializeMap(latitude, longitude);
        },
        (error) => {
          console.error("Error getting location: ", error.message);
          alert("Unable to retrieve your location. Using default location.");
          initializeMap(37.7749, -122.4194);
        },
        { enableHighAccuracy: true, maximumAge: 0, timeout: 5000 }
      );
    } else {
      alert("Geolocation is not supported by this browser.");
      initializeMap(37.7749, -122.4194);
    }
  }, []);

  function handleMapClick(location) {
    const newBox = {
      position: location,
      expanded: false,
      editing: true, // Show form initially
      reason: "",
      timerValue: "",
    };

    setMarkedLocations((prev) => [...prev, newBox]);
  }

  function toggleBox(index) {
    setMarkedLocations((prev) =>
      prev.map((box, i) =>
        i === index ? { ...box, expanded: !box.expanded } : box
      )
    );
  }

  function handleInputChange(index, field, value) {
    setMarkedLocations((prev) =>
      prev.map((box, i) =>
        i === index ? { ...box, [field]: value } : box
      )
    );
  }

  function handleSubmit(index) {
    setMarkedLocations((prev) =>
      prev.map((box, i) =>
        i === index ? { ...box, editing: false, timerValue: parseInt(box.timerValue, 10) * 60 } : box
      )
    );
  }

  function removeBox(index) {
    setMarkedLocations((prev) => prev.filter((_, i) => i !== index));
  }

  return (
    <div>
      <div ref={mapRef} style={{ width: "100%", height: "500px", position: "relative" }} />

      {markedLocations.map((box, index) => (
        <div
          key={index}
          onClick={() => toggleBox(index)}
          style={{
            position: "absolute",
            top: 100 + index * 50, // Adjust this to position near markers
            left: 100 + index * 50,
            backgroundColor: "blue",
            width: box.expanded ? "200px" : "15px",
            height: box.expanded ? "130px" : "15px",
            borderRadius: "5px",
            color: "white",
            padding: box.expanded ? "10px" : "0",
            cursor: "pointer",
          }}
        >
          {box.expanded ? (
            box.editing ? (
              <div>
                <input
                  type="text"
                  placeholder="Enter reason"
                  value={box.reason}
                  onChange={(e) => handleInputChange(index, "reason", e.target.value)}
                  style={{ width: "100%", marginBottom: "5px" }}
                />
                <input
                  type="number"
                  placeholder="Set timer (min)"
                  value={box.timerValue}
                  onChange={(e) => handleInputChange(index, "timerValue", e.target.value)}
                  style={{ width: "100%", marginBottom: "5px" }}
                />
                <button onClick={(e) => {
                  e.stopPropagation();
                  handleSubmit(index);
                }}>Start</button>
              </div>
            ) : (
              <>
                <p><strong>Reason:</strong> {box.reason}</p>
                <Timer
                  initialTime={box.timerValue}
                  onComplete={() => removeBox(index)}
                />
                <button onClick={(e) => {
                  e.stopPropagation();
                  removeBox(index);
                }} style={{ marginTop: "5px" }}>
                  Done
                </button>
              </>
            )
          ) : null}
        </div>
      ))}
    </div>
  );
};

export default Map;
