import React, { useEffect, useRef } from "react";

const Map = ({ markers, boxes }) => {
  const mapRef = useRef(null); // Map container reference
  const googleMap = useRef(null); // Google Map instance
  const markerInstances = useRef([]); // Stores markers
  const userCircle = useRef(null);
  const rectangleInstances = useRef([]); // Stores rectangles

  function initializeMap(latitude, longitude) {
    if (!googleMap.current) {
      googleMap.current = new window.google.maps.Map(mapRef.current, {
        center: { lat: latitude, lng: longitude },
        zoom: 17,
      });
    }
  }

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.watchPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          initializeMap(latitude, longitude);
          updateUserCircle(latitude, longitude);
        },
        (error) => {
          console.error("Error getting location: ", error.message);
          alert("Unable to retrieve your location. Using default location.");
          initializeMap(37.7749, -122.4194);
        }
      );
    } else {
      alert("Geolocation is not supported by this browser.");
      initializeMap(37.7749, -122.4194);
    }
  }, []);

  function updateUserCircle(lat, lng) {
    if (!googleMap.current) return;

    if (!userCircle.current) {
      userCircle.current = new window.google.maps.Circle({
        center: { lat, lng },
        radius: 20,
        strokeColor: "blue",
        strokeOpacity: 0.5,
        strokeWeight: 2,
        fillColor: "blue",
        fillOpacity: 0.3,
        map: googleMap.current,
      });
    } else {
      userCircle.current.setCenter({ lat, lng });
    }
  }

  useEffect(() => {
    if (googleMap.current && markers) {
      markerInstances.current.forEach((marker) => marker.setMap(null));
      markerInstances.current = [];

      markers.forEach((marker) => {
        const newMarker = new window.google.maps.Marker({
          position: marker,
          map: googleMap.current,
        });
        markerInstances.current.push(newMarker);
      });
    }
  }, [markers]);

  // ✅ Function to Add Rectangles (Boxes)
  useEffect(() => {
    if (googleMap.current && boxes) {
      rectangleInstances.current.forEach((rectangle) => rectangle.setMap(null));
      rectangleInstances.current = [];

      boxes.forEach((box) => {
        const rectangle = new window.google.maps.Rectangle({
          bounds: box.bounds,
          strokeColor: "red",
          strokeOpacity: 0.8,
          strokeWeight: 2,
          fillColor: "red",
          fillOpacity: 0.3,
          map: googleMap.current,
        });
        rectangleInstances.current.push(rectangle);
      });
    }
  }, [boxes]);

  return (
    <div ref={mapRef} style={{ width: "100%", height: "500px" }} />
  );
};

export default Map;
