import React, { useEffect, useRef, useState } from "react";
import BoxComponent from "./components/BoxComponent"; // Import the Box Component

const Map = () => {
  const mapRef = useRef(null);
  const googleMap = useRef(null);
  const userCircle = useRef(null);
  const [userPosition, setUserPosition] = useState(null);
  const [markedLocations, setMarkedLocations] = useState([]);

  function initializeMap(latitude, longitude) {
    if (!googleMap.current) {
      googleMap.current = new window.google.maps.Map(mapRef.current, {
        center: { lat: latitude, lng: longitude },
        zoom: 17,
      });
    }
  }

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.watchPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          setUserPosition({ lat: latitude, lng: longitude });
          initializeMap(latitude, longitude);
          updateUserCircle(latitude, longitude);
        },
        (error) => {
          console.error("Error getting location: ", error.message);
          alert("Unable to retrieve your location. Using default location.");
          initializeMap(37.7749, -122.4194);
        },
        { enableHighAccuracy: true, maximumAge: 0, timeout: 5000 }
      );
    } else {
      alert("Geolocation is not supported by this browser.");
      initializeMap(37.7749, -122.4194);
    }
  }, []);

  function updateUserCircle(lat, lng) {
    if (!googleMap.current) return;

    if (!userCircle.current) {
      userCircle.current = new window.google.maps.Circle({
        center: { lat, lng },
        radius: 20,
        strokeColor: "blue",
        strokeOpacity: 0.5,
        strokeWeight: 2,
        fillColor: "blue",
        fillOpacity: 0.3,
        map: googleMap.current,
      });
    } else {
      userCircle.current.setCenter({ lat, lng });
    }
  }

  function markLocation() {
    if (!userPosition) {
      alert("User location not available.");
      return;
    }

    const reason = prompt("Enter reason for parking:");
    if (!reason) return;

    const timer = prompt("Set a timer in minutes:");
    const timerValue = parseInt(timer, 10);
    if (isNaN(timerValue) || timerValue <= 0) {
      alert("Invalid timer value.");
      return;
    }

    const newBox = {
      reason,
      timerValue,
      position: { lat: userPosition.lat, lng: userPosition.lng },
    };

    setMarkedLocations((prev) => [...prev, newBox]);

    setTimeout(() => {
      setMarkedLocations((prev) => prev.filter((b) => b !== newBox));
      alert(`Time's up for parking: ${reason}`);
    }, timerValue * 60000);
  }

  return (
    <div>
      <button onClick={markLocation} style={{ margin: "10px", padding: "10px" }}>
        Mark Location
      </button>
      <div ref={mapRef} style={{ width: "100%", height: "500px", position: "relative" }} />

      {markedLocations.map((box, index) => (
        <BoxComponent
          key={index}
          reason={box.reason}
          timerValue={box.timerValue}
          position={{ x: 100 + index * 50, y: 100 + index * 50 }} // Adjust positioning near map
          onRemove={() => {
            setMarkedLocations((prev) => prev.filter((b, i) => i !== index));
          }}
        />
      ))}
    </div>
  );
};

export default Map;
